  CONSTRAINT `pcb_news_collection_ibfk_2` FOREIGN KEY (`posted_by`) REFERENCES `pcb_users` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB AUTO_INCREMENT=51 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

